import { StyleSheet, Dimensions } from 'react-native';

const stylesChatBox = StyleSheet.create({
    chatRoomContainer: {
        flex: 1,
        backgroundColor: '#264fa2',
        alignItems: 'center',
        justifyContent: 'center',
        maxHeight: Dimensions.get('window').height,

    },
    messagesContainer: {
        flex: 1,
        flexDirection: 'column',
        flexGrow: 1,
        justifyContent: 'flex-start',
        backgroundColor: '#fff',



    },
    SentMessageContainer: {
        flex: 1,
        flexDirection: 'row-reverse',
        justifyContent: 'flex-start',
        alignItems: 'center',
        marginTop: 25,
        marginBottom: 25,
        flexGrow: 1,
        minHeight: 'auto'
    },
    SentMessageText:{
        fontSize: 20,
        backgroundColor: '#f39c12',
        color: 'black',
        marginRight: 10,
        maxWidth: 500,
        borderRadius: 25,
        padding: 8,
        alignSelf: 'center'
    },
    ReceivedMessageContainer: {
        flex: 1,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'flex-start',
        marginTop: 50,
        flexGrow: 1,
        minHeight: 'auto'
 
    },
    ReceivedMessageText: {
        fontSize: 20,
        backgroundColor: '#3498db',
        color: '#fff',
        marginLeft: 10,
        maxWidth: 500,
        borderRadius: 25,
        padding: 8,
        alignSelf: 'center'
    },
    profileImage :{
        width: 50,
        height: 50,
        borderRadius: 25,
        alignSelf: 'center'
    }
});
export default stylesChatBox;
