import React, { useRef, useState} from 'react'
import { StyleSheet, Text, View, useWindowDimensions, Animated, PixelRatio,TouchableOpacity } from 'react-native'
import CreateRoomScreen from './CreateRoomScreen';
import JoinRoomScreen from './JoinRoomScreen';
//import { TouchableOpacity} from 'react-native-gesture-handler'
export default function SideBarMenu(props) {

    const { width, height } = useWindowDimensions();

    const [createRoom, showCreateRoom] = useState(false);
    const [joinRoom, showJoinRoom] = useState(false);
    var widthModifier = 0.6;
    if (width < 1280) {
        widthModifier = 0.8;
    }
    else {
        widthModifier = 0.3;
    }
    const fontRatio = PixelRatio.getFontScale()

    var slideAnim = useRef(new Animated.Value(-widthModifier * width * 2)).current;

    const slideIn = () => {
        Animated.timing(slideAnim, {
            toValue: 0,
            duration: 500,
            useNativeDriver: false
        }).start();
    };
    const slideOut = () => {
        Animated.timing(slideAnim, {
            toValue: -widthModifier* 1.1 * width,
            duration: 500,
            useNativeDriver: false
        }).start();
    };

    if(props.showMenu == true){
        slideIn()
    }
    const slideOutView = () =>{
        props.setShowMenu(false)
        slideOut()
    }

    const styles = StyleSheet.create({
        SideBarContainer: {
            flex: 1,
            flexDirection: 'column',
            justifyContent: 'flex-start',
            alignItems: 'center',
            position: 'absolute',
            top: 0,
            backgroundColor: '#46494d',
            minWidth: widthModifier * width,
            maxWidth: widthModifier * width,
            minHeight: height,
            maxHeight: height,
            zIndex: 20,
            borderTopRightRadius: 20,
            borderBottomRightRadius: 20,
            shadowColor: '#000',
            shadowOffset: { width: 3* fontRatio, height: 0* fontRatio, },
            shadowOpacity: 0.7,
            shadowRadius: 50* fontRatio,
            elevation: 5* fontRatio,
        },
        SiderBarHeader: {
            flex: 1,
            flexDirection: 'row',
            justifyContent: 'center',
            alignItems: 'center',
            minHeight: 0.1 * height,
            maxHeight: 0.1 * height,
            width: widthModifier * width
        },
        MenuTitleText: {
            fontSize: 25,
            color: '#fff',
            marginRight: 'auto',


        },
        MenuItemsContainer: {
            flex: 1,
            flexDirection: 'column',
            justifyContent: 'flex-start',
            alignItems: 'center',
            minHeight: widthModifier * height,
            maxHeight: widthModifier * height,
            minWidth: 0.6 * width,
            maxWidth: 0.6 * width,

        },
        MenuItem: {
            flex: 1,
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            backgroundColor: '#2DA8D8FF',
            minWidth: (widthModifier - 0.02) * width,
            maxWidth: (widthModifier - 0.02) * width,
            minHeight: 0.05 * height,
            maxHeight: 0.05 * height,
            marginBottom: 20* fontRatio,
            borderRadius: 25,

        },
        MenuItemText: {
            fontSize: 20,
            color: '#fff'
        },
        CloseMenuButton: {
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center',
            fontSize: 30,
            marginRight: 'auto',
            marginLeft: 10* fontRatio,
            backgroundColor: '#2DA8D8FF',
            width: 40* fontRatio,
            maxWidth: 40* fontRatio,
            height: 40* fontRatio,
            borderRadius: 25
        },
        CloseMenuText: {
            fontSize: 20,
            color: '#fff',
            padding: 5* fontRatio,
        }

    });

    return (
        <Animated.View style={[styles.SideBarContainer, { left: slideAnim }]}>
            { createRoom ?<CreateRoomScreen showCreateRoom={showCreateRoom} /> : null}
            { joinRoom ?<JoinRoomScreen showJoinRoom={showJoinRoom} /> : null}
            <View style={styles.SiderBarHeader}>
                <TouchableOpacity onPress={() =>slideOutView()} style={styles.CloseMenuButton}>
                    <Text style={styles.CloseMenuText}>X</Text>
                </TouchableOpacity>
                <Text style={styles.MenuTitleText}>Menu</Text>
            </View>
            <View style={styles.MenuItemsContainer}>
                <TouchableOpacity style={styles.MenuItem} onPress={() => showJoinRoom(true)}>
                    <Text style={styles.MenuItemText}>Join Room</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.MenuItem} onPress={() => showCreateRoom(true)}>
                    <Text style={styles.MenuItemText}>Create Room</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.MenuItem}>
                    <Text style={styles.MenuItemText}>Dummy Option</Text>
                </TouchableOpacity>
            </View>
        </Animated.View>
    )
}
