import React, { useState } from 'react'
import { ImageBackground, StyleSheet, KeyboardAvoidingView } from 'react-native'
import { theme } from '../core/theme'
import { useWindowDimensions } from 'react-native'


const Background = ({ children, widthModifier}) => {
  const { width } = useWindowDimensions()
  return (
    <ImageBackground
      source={require('../assets/background_dot.png')}
      resizeMode="repeat"
      style={styles.background}
    >
      <KeyboardAvoidingView style={[styles.container, containerWidth(width, widthModifier)]} behavior="padding">
        {children}
      </KeyboardAvoidingView>
    </ImageBackground>
  )
}

const containerWidth = (width, widthModifier) => {
  return {
    minWidth: widthModifier * width,
    maxWidth: widthModifier * width
  }
}
const styles = StyleSheet.create({
  background: {
    flex: 1,
    width: '100%',
    backgroundColor: theme.colors.background,
  },
  container: {
    flex: 1,
    padding: 20,
    width: '100%',
    alignSelf: 'center',
    alignItems: 'center',
    justifyContent: 'center',
    textAlign: 'center'
  },
})

export default Background
