import React, { useContext, useState } from 'react'
import { View, StyleSheet, Text, Dimensions, Image, useWindowDimensions, PixelRatio, TouchableOpacity} from 'react-native'
import AuthContext from '../contexts/AuthContext';
//import { TouchableOpacity} from 'react-native-gesture-handler'

import SideBarMenu from './SideBarMenu';

export default function NavBar(props) {
    const {width, height} = useWindowDimensions();
    const [dropdownVisible, setDropdownVisible] = useState(false);
    const [showMenu, setShowMenu] = useState(false);
    const ratio = PixelRatio.get();
    const fontRatio = PixelRatio.getFontScale()
    const userInfo = () => {
        if (props.user != null) {
            const { auth } = useContext(AuthContext);
            const { displayName, photoURL } = auth.currentUser

            return (
                <View style={styles.UserInfoContainer}>
                    <TouchableOpacity onPress={() => setDropdownVisible(!dropdownVisible)}>
                        <Image style={styles.UserProfilePic} source={{ uri: photoURL }}></Image>
                    </TouchableOpacity>
                    <Text style={styles.UserNameTitle}>{displayName}</Text>
                </View>
            )
        }
    }


    const SignOut = () => {
        if (props.user != null) {
            const { auth } = useContext(AuthContext);
            return auth.currentUser && (
                <View style={styles.AccountDropdownView}>
                    <TouchableOpacity style={styles.SignOutButton} onPress={() => auth.signOut()}>
                        Sign Out
                    </TouchableOpacity>
                </View>
            )
        }
    }

    const styles = StyleSheet.create({
        NavContainer: {
            position: 'absolute',
            top: 0,
            flex: 1,
            flexDirection: 'row',
            backgroundColor: '#2DA8D8FF',
            justifyContent: 'flex-start',
            alignItems: 'center',
            minWidth: width,
            maxWidth: width,
            minHeight: 0.07*height,
            maxHeight: 0.07*height,
            overflow: 'visible',
            zIndex: 15
        },
        LogoText: {
            fontSize: 20,
            color: '#fff',
    
        },
        UserInfoContainer: {
            flex: 1,
            flexDirection: 'row-reverse',
            justifyContent: 'flex-start',
            alignItems: 'center',
            marginLeft: 'auto',
            marginRight: 2* ratio,
    
        },
        UserNameTitle: {
            paddingRight: '1dp',
            fontSize: 20* fontRatio,
            color: '#fff',
    
        },
        UserProfilePic: {
            width: 50* ratio,
            height: 50 * ratio,
            borderRadius: 25
        },
        AccountDropdownView: {
            marginRight: 1 * ratio,
            flex: 1,
            flexDirection: 'column',
            alignItems: 'center',
            backgroundColor: '#fff',
            maxWidth: 5 * ratio,
            borderRadius: 10 
    
    
        },
        SignOutButton: {
            fontSize: 18,
            padding: 10* ratio,
            marginRight: 20 * ratio
        },
        OpenMenuButton: {
            flex:1,
            justifyContent: 'center',
            alignItems: 'center',
            fontSize: 30,
            marginLeft: 0.3 * ratio,
            backgroundColor: '#2DA8D8FF',
            width: 40 * ratio,
            maxWidth: 40* ratio,
            height: 40* ratio,
            borderRadius: 25
        },
        OpenMenuText: {
            fontSize: 20,
            color: '#fff',
            padding: 5* ratio,
        },
        
    })
    console.log('heyyy bitchess')
    console.log('menu ' +showMenu)
    console.log(dropdownVisible)

    return (
        <View style={styles.NavContainer}>
            <TouchableOpacity onPress={() => setShowMenu(true)} style={styles.OpenMenuButton}> 
                <Text style={styles.OpenMenuText}>☰</Text>
            </TouchableOpacity>
            <SideBarMenu showMenu={showMenu} setShowMenu={setShowMenu}/>
            <Text style={styles.LogoText}>Chatter</Text>
            {userInfo()}
            {dropdownVisible ? SignOut() : null}
        </View>
    )
}
