import React, { useState, useContext } from 'react'
import { StyleSheet, Text, View, Image, useWindowDimensions, PixelRatio, Platform, TextInput } from 'react-native'
import { TouchableOpacity } from 'react-native-gesture-handler'
import * as GoogleSignIn from 'expo-google-sign-in';
import firebase from 'firebase'
import 'firebase/auth'
import AuthContext from '../contexts/AuthContext';

function Login() {


    const [containerStyle, setContainerStyle] = useState('container');
    const [errorMessageLogin, setErrorLogin] = useState('');
    const [errorMessageSignup, setErrorSignup] = useState('');


    const [loginInput = {
        'email': '',
        'password': ''
    },
        setLoginInput] = useState({});

    const [signupInput = {
        'name': '',
        'email': '',
        'password': ''
    },
        setSignupInput] = useState({});


    const handleLoginChange = e => {
        loginInput[e.target.name] = e.target.value
        setLoginInput(loginInput)
    }

    const handleSignupChange = e => {
        signupInput[e.target.name] = e.target.value
        setSignupInput(signupInput)
    }

    /*
    const handleLogin = e => {
        e.preventDefault();
        fetch('http://localhost:9000/account/login', {
            method: "POST",
            headers: {
                'Content-type': 'application/json'
            },
            body: JSON.stringify(loginInput)
        })
            .then(function (response) {
                return response.json();
            }).then(function (data) {
                if (data.message.toUpperCase() == 'OK') {
                    console.log('OK')
                    Cookies.set('accountAuth', data.cookieAuth, { expires: 7 })
                    setUser(loginInput.email)
                    history.push("/");
                }
                else {
                    setErrorLogin(data.message);
                }
            });

    }
    const handleSignup = e => {
        e.preventDefault();
        fetch('http://localhost:9000/account/register', {
            method: "POST",
            headers: {
                'Content-type': 'application/json'
            },
            body: JSON.stringify(signupInput)
        })
            .then(function (response) {
                return response.json();
            }).then(function (data) {
                if (data.message.toUpperCase() == 'OK') {
                    Cookies.set('accountAuth', data.cookieAuth, { expires: 7 })
                    setUser(signupInput.email)
                    history.push("/");
                }
                else {
                    setErrorSignup(data.message);
                }
            });
    }
    */

    const changeSide = (rightSide) => {
        const container = document.getElementById('container');
        console.log(container)
        console.log(rightSide)
        if (rightSide) {
            setContainerStyle('container right-panel-active');
        }
        else {
            setContainerStyle('container');
        }
    }

    const styles = StyleSheet.create({
        Header: {
            fontWeight: 'bold',
            margin: 0
        },
        Button: {
            "borderTopLeftRadius": 10,
            "borderTopRightRadius": 10,
            "borderBottomRightRadius": 10,
            "borderBottomLeftRadius": 10,
            "borderWidth": 1,
            "borderColor": "#2e86c1",
            "borderStyle": "solid",
            "backgroundColor": "#2e86c1",
            "color": "#ffffff",
            "fontSize": 12,
            "fontWeight": "bold",
            "paddingTop": 12,
            "paddingRight": 45,
            "paddingBottom": 12,
            "paddingLeft": 45,
            "letterSpacing": 1,
            "textTransform": "uppercase",
            "transition": "transform 80ms ease-in"
        },
        Transparent: {
            borderColor: 'white',
            backgroundColor: 'transparent'
        },
        FormContainer: {
            "backgroundColor": "#ffffff",
            "display": "flex",
            "alignItems": "center",
            "justifyContent": "center",
            "flexDirection": "column",
            "paddingTop": 0,
            "paddingRight": 50,
            "paddingBottom": 0,
            "paddingLeft": 50,
            "height": "100%",
            "textAlign": "center"
        },
        TextInput: {
            "backgroundColor": "#eee",
            "borderWidth": 0,
            "borderColor": "black",
            "borderStyle": "solid",
            "paddingTop": 12,
            "paddingRight": 15,
            "paddingBottom": 12,
            "paddingLeft": 15,
            "marginTop": 8,
            "marginRight": 0,
            "marginBottom": 8,
            "marginLeft": 0,
            "width": "100%"
        },
        LoginBackground: {
            "backgroundColor": "#808b96",
            "flexGrow": 1,
            "flexShrink": 1,
            "flexBasis": "auto",
            "position": "relative",
            "display": "flex",
            "justifyContent": "center",
            "alignItems": "center"
        },
        ContainerClass: {
            //missing box shadow   box-shadow: 0 14px 28px rgba(0, 0, 0, 0.25), 0 10px 10px rgba(0, 0, 0, 0.22);
            "backgroundColor": "#ffffff",
            "borderTopLeftRadius": 10,
            "borderTopRightRadius": 10,
            "borderBottomRightRadius": 10,
            "borderBottomLeftRadius": 10,
            "position": "relative",
            "overflow": "hidden",
            "width": 768,
            "maxWidth": "100%",
            "minHeight": 480
        },
        FormContainer: {
            "position": "absolute",
            "top": 0,
            "height": "100%",
            //"transition": "all 0.6s ease-in-out"
        },
        LoginContainer: {
            left: 0,
            width: '50%',
            zIndex: 2
        },
        SignUpContainer: {
            left: 0,
            width: '50%',
            opacity: 0,
            zIndex: 1
        },
        ErrorMessage: {
            "color": "#d32f2f",
            "fontWeight": "bold"
        },
        //.container.right-panel-active .login-container
        RightPanelActiveLogin: {
            transform: [
                { translateX: '100%' }
            ]
        },
        RightPanelActiveSignup: {
            transform: [
                { translateX: '100%' }
            ],
            opacity: 1,
            zIndex: 5,
            //animation: show 0.6s
        },
        //missing keyframes
        OverlayContainer: {
            "position": "absolute",
            "top": 0,
            "left": "50%",
            "width": "50%",
            "height": "100%",
            "overflow": "hidden",
            //"transition": "transform 0.6s ease-in-out",
            "zIndex": 100
        },
        RightPanelActiveOverlayContainer: {
            transform: [
                { translateX: '-100%' }
            ],
        },
        Overlay: {
            /*
                background: -webkit-linear-gradient(to right, #21618c, #2e86c1 );
                background: linear-gradient(to right, #21618c,  #2e86c1 );
            */
            "backgroundColor": "#ff416c",
            "backgroundRepeat": "no-repeat",
            "backgroundSize": "cover",
            "backgroundPosition": "0 0",
            "color": "#ffffff",
            "position": "relative",
            "left": "-100%",
            "height": "100%",
            "width": "200%",
            "transform": [
                {
                    "translateX": 0
                }
            ],
            "transition": "transform 0.6s ease-in-out"
        },
        RightPanelActiveOverlay: {
            "transform": [
                {
                    "translateX": '50%'
                }
            ],
        },
        OverlayPanel: {
            "position": "absolute",
            "display": "flex",
            "alignItems": "center",
            "justifyContent": "center",
            "flexDirection": "column",
            "paddingTop": 0,
            "paddingRight": 40,
            "paddingBottom": 0,
            "paddingLeft": 40,
            "textAlign": "center",
            "top": 0,
            "height": "100%",
            "width": "50%",
            "transform": [
                {
                    "translateX": 0
                }
            ],
            //"transition": "transform 0.6s ease-in-out"
        },
        NewMemberPanel: {
            transform: [{
                translateX: '-20%'
            }]
        },
        RightPanelActiveNewMember: {
            transform: [{
                translateX: 0
            }]
        },
        ReturningMemberPanel: {
            "right": 0,
            "transform": [
                {
                    "translateX": 0
                }
            ]
        },
        RightPanelActiveReturningMember: {
            "transform": [
                {
                    "translateX": '20%'
                }
            ]
        }









    })

    return (
        <View style={styles.LoginBackground}>
            <View style={styles.ContainerClass}>
                <View style={styles.FormContainer, styles.LoginContainer}>
                    <Text style={styles.Header}>Sign In</Text>
                    <Text>Email</Text>
                    <TextInput style={styles.TextInput} type="text" id="email" name="email" onChange={handleLoginChange} />
                    <Text>Password</Text>
                    <TextInput style={styles.TextInput} type="password" id="password" name="password" onChange={handleLoginChange} />
                    <Text style={styles.ErrorMessage}>{errorMessageLogin}</Text>
                    <TouchableOpacity style={styles.Button} onPress={handleLogin} type="submit" >
                        <Text>Sign In</Text>
                    </TouchableOpacity>
                </View>

                <View style={styles.FormContainer, styles.SignUpContainer}>
                    <Text style={styles.Header}>Create an account</Text>
                    <Text>Name</Text>
                    <TextInput type="text" id="name" name="name" onChange={handleSignupChange} />
                    <Text>Email</Text>
                    <TextInput style={styles.TextInput} type="text" id="email" name="email" onChange={handleSignupChange} />
                    <Text>Password</Text>
                    <TextInput style={styles.TextInput} type="text" id="password" name="password" onChange={handleSignupChange} />
                    <Text style={styles.ErrorMessage}>{errorMessageSignup}</Text>
                    <TouchableOpacity style={styles.Button} onPress={handleSignup} type="submit">
                        <Text>Sign Up</Text>
                    </TouchableOpacity>

                </View>

                <View style={styles.OverlayContainer}>
                    <View style={styles.Overlay}>
                        <View style={styles.OverlayPanel, styles.NewMemberPanel}>
                            <Text style={styles.Header}>Hi there!</Text>
                            <Text>Hi. Just enter a few details and we will create your account.</Text>
                            <Text>Already have an account?</Text>
                            <TouchableOpacity style={styles.Transparent, styles.Button} id='login' onClick={() => changeSide(false)}>
                                <Text>Sign In</Text>
                            </TouchableOpacity>
                        </View >
                        <View style={styles.OverlayPanel, styles.ReturningMemberPanel} >
                            <Text style={styles.Header}>Welcome Back!</Text>
                            <Text>Hi. Great to see you again.</Text>
                            <Text>Don't have an account?</Text>
                            <TouchableOpacity style={styles.Transparent, styles.Button} id='signUp' onClick={() => changeSide(true)}>
                                <Text>Create Account</Text>
                            </TouchableOpacity >
                        </View >
                    </View >
                </View >
            </View >
        </View >
    )
}

export default Login;
