import React, { useState, useContext } from 'react'
import { StyleSheet, Text, View, Image, useWindowDimensions, PixelRatio, Platform, TextInput } from 'react-native'
import { TouchableOpacity, } from 'react-native-gesture-handler'
import * as GoogleSignIn from 'expo-google-sign-in';
import firebase from 'firebase'
import 'firebase/auth'
import AuthContext from '../contexts/AuthContext';

export default function LoginScreen(props) {

    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const { auth, setLoadingApp } = useContext(AuthContext)
    const signInWithGoogle = () => {
        if (Platform.OS == 'android' || Platform.OS == 'ios') {
            props.googleLogin()
        }
        else {
            console.log('helllooo there phone')
            const provider = new firebase.auth.GoogleAuthProvider();
            auth.signInWithRedirect(provider);
        }
    }
    const signInWithEmail = () => {
        auth.signInWithEmailAndPassword(email, password)
    }
    const signUpWithEmail = () => {
        auth.createUserWithEmailAndPassword(email, password)
    }
    const fontRatio = PixelRatio.getFontScale()

    const { width, height } = useWindowDimensions()
    const styles = StyleSheet.create({
        LoginContainer: {


            minHeight: 0.93 * height,
            maxHeight: 0.93 * height,
            width: width

        },
        LoginTitle: {
            fontSize: 40,
            color: '#fff',
            marginTop: 10,
        },
        loginButton: {
            backgroundColor: '#D9514EFF',
            borderRadius: 25,
            marginBottom: 20,
            height: 100


        },
        LoginText: {
            padding: 10 * fontRatio,
            color: '#fff',
            fontSize: 30

        },
        CredentialsTextInput: {
            height: 70,
            width: 200,
            fontSize: 15 * fontRatio,
            backgroundColor: '#d5d8dc',
            borderRadius: 20,
            marginBottom: 10,
            textAlign: 'center',
            zIndex: 100
        },

    })

    return (
        <View style={styles.LoginContainer}>
            <Text style={styles.LoginTitle}>Login</Text>
            <TextInput placeholder='Enter email' onChangeText={text =>setEmail(text)} style={styles.CredentialsTextInput} value={email}></TextInput>
            <TextInput placeholder='Enter password' onChangeText={text => setPassword(text)} style={styles.CredentialsTextInput} value={password}></TextInput>
            <TouchableOpacity onPress={() => signInWithEmail()} style={styles.loginButton}>
                <Text style={styles.LoginText}>Sign in with email</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => signUpWithEmail()} style={styles.loginButton}>
                <Text style={styles.LoginText}>Sign up with email</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => signInWithGoogle()} style={styles.loginButton}>
                <Text style={styles.LoginText}>Sign in with Google</Text>
            </TouchableOpacity>
        </View>
    )
}
