import React, { useContext, useEffect, useState } from 'react'
import { useDocumentData } from 'react-firebase-hooks/firestore'
import { StyleSheet, Text, View } from 'react-native'
import AuthContext from '../contexts/AuthContext'
import RoomIcon from './RoomIcon'

export default function UsersRooms(props) {

    const { auth, firestore } = useContext(AuthContext)
    const userRef = firestore.collection('users').doc(auth.currentUser.uid)
    const [userDetails] = useDocumentData(userRef)


    return (
        <View style={styles.RoomsContainer}>
            {userDetails ? userDetails.currentRooms.map((roomCode, i) => <RoomIcon key={i} roomCode={roomCode} />) : null}
        </View>
    )
}
// {usersRooms.map((roomCode, i) => <RoomIcon key={i} roomCode={roomCode} />)}

const styles = StyleSheet.create({
    RoomsContainer: {
        position: 'absolute',
        top: 0,
        left: 0,
        flex: 1,
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignContent: 'center',
        backgroundColor: '#fff',
        minHeight: 900,
        maxWidth: 90,
        minWidth: 90,
        zIndex: 100

    }
})
