import React, { useContext } from 'react'
import { StyleSheet, Text, View } from 'react-native'
import AuthContext from '../contexts/AuthContext'
import RoomIcon from './RoomIcon'

export default function UsersRooms(props) {
    const { auth, firestore } = useContext(AuthContext)


    const getRooms = () => {
        var currentRooms = []
        if (auth.currentUser != null) {
            const userRef = firestore.collection('users').doc(auth.currentUser.uid)
            userRef.get()
                .then((docSnapShot) => {
                    console.log(docSnapShot)
                    if (docSnapShot.exists) {
                        console.log(docSnapShot.data().currentRooms)
                        currentRooms = docSnapShot.data().currentRooms
                    }
                })
        }
        console.log('here: ' + currentRooms)
        return currentRooms && currentRooms.map((roomCode, i) => <RoomIcon key={i} roomCode={roomCode} />)
    }

    return (
        <View style={styles.RoomsContainer}>
            {getRooms()}
        </View>
    )
}

const styles = StyleSheet.create({
    RoomsContainer: {
        position: 'absolute',
        top: 0,
        left: 0,
        flex: 1,
        flexDirection: 'column',
        justifyContent: 'flex-start',
        alignContent: 'center',
        backgroundColor: '#fff',
        minHeight: 900,
        maxWidth: 90,
        minWidth: 90,
        zIndex: 100

    }
})
