import React, { useContext, useState } from 'react'
import { StyleSheet, Text, View, Image, TextInput, TouchableOpacity } from 'react-native';
import AuthContext from '../contexts/AuthContext';
import firebase from 'firebase'
import 'firebase/firestore'


export default function SendMessageForm(props){
    const [messageText, onChangeText] = useState('')
    const {auth} = useContext(AuthContext)


    const sendMessage = async() =>{
        const {uid, photoURL} = auth.currentUser
        await props.messageRef.add({
            text: messageText,
            createdAt: firebase.firestore.FieldValue.serverTimestamp(),
            uid,
            photoURL

        }); 
        onChangeText('')
    }
    return(
        <View style={() => {uid === auth.currentUser.uid ? styles.SentMessage : styles.ReceivedMessage}}>
            <TextInput onChangeText={text => onChangeText(text)} placeholder='Enter your message'></TextInput>
            <TouchableOpacity onPress={() => sendMessage()}>
                <Text>Send</Text>
            </TouchableOpacity>
        </View>
    )
}

const styles = StyleSheet.create({
    SentMessage: {

    },
    ReceivedMessage : {

    }
  });
