import React, { useContext, useState, } from 'react'
import { StyleSheet, Text, View, Image, TextInput, Dimensions, useWindowDimensions } from 'react-native';
import { TouchableOpacity} from 'react-native-gesture-handler'
import AuthContext from '../contexts/AuthContext';
import firebase from 'firebase'
import 'firebase/firestore'


export default function SendMessageForm(props){
    const {width, height} = useWindowDimensions()
    const [messageText, onChangeText] = useState('')
    const {auth, messageRef} = useContext(AuthContext)


    const sendMessage = async() =>{
        const {uid, photoURL} = auth.currentUser
        await messageRef.add({
            text: messageText,
            createdAt: firebase.firestore.Timestamp.now(),
            uid,
            photoURL
        }); 
        onChangeText('')
    }

    const styles = StyleSheet.create({
        NewMessageContainer: {
            position: 'relative',
            bottom: 0,
            flex: 1,
            flexDirection: 'row',
            justifyContent: 'flex-start',
            alignContent: 'center',
            minHeight:  0.07*height,
            maxHeight: 0.07*height,
            maxWidth: width,
            backgroundColor: '#fff',
            borderTopLeftRadius: 15,
            zIndex: 2,
            flexBasis: '100%'
        },
        TextInput: {
            fontSize: 20,
            maxWidth: 0.85*width,
            padding: 7,
            alignSelf: 'stretch'
    
        },
        SendMessageContainer: {
            flex: 1,
            flexDirection: 'row',
            justifyContent: 'center',
            alignItems: 'center',
            backgroundColor: '#2DA8D8F',
            minWidth: 0.15*width
        },
        SendText: {
            color: '#fff'
        }
      });

    return(
        <View style={styles.NewMessageContainer}>
            <TextInput multiline={true} onSubmitEditing={() => sendMessage()} style={styles.TextInput} value={messageText} onChangeText={text => onChangeText(text)} placeholder='Enter your message' t></TextInput>
            <TouchableOpacity style={styles.SendMessageContainer} onPress={() => sendMessage()}>
                <Text style={styles.SendText}>Send</Text>
            </TouchableOpacity>
        </View>
    )


}
