import React, { useContext, useEffect, useState } from 'react'
import { StyleSheet, Text, View } from 'react-native';
import AuthContext from '../contexts/AuthContext'

import firebase from 'firebase/app';
import firestore from 'firebase/firestore';
import { useCollectionData } from 'react-firebase-hooks/firestore'

const io = require('socket.io-client')
const socketEndpoint = 'http://192.168.10.118:4167'

export default function ChatBox() {

  //firestore setup
  const messageRef = firestore.collection('messages');
  const query = messageRef.orderBy('createdAt').limit(25);
  const [messages] = useCollectionData(query, {idField: 'id'})

  //socket.io states
  const [connected, setConnected] = useState('false');
  const [pingData, setPingData] = useState('');

  //firebase user auth
  const auth = useContext(AuthContext);
  const {displayName} = auth.currentUser;

  useEffect(() => {

    var socket = io(socketEndpoint);
    socket.on('connectClient', () => {
      setConnected('true');
    })
    socket.on('ping', (data) => {
      setPingData(data.date);
    })

  }, [])

  //sign out firebase user
  const SignOut = () => {
    return auth.currentUser && (
      <button onClick={() => auth.signOut()}>Sign Out</button>
    )
  }
  
  return (
    <View style={stylesChatBox.chatRoomContainer}>
      <Text>Chat Screen</Text>
      <Text>{displayName}</Text>
      {SignOut()}
      <View style={stylesChatBox.messagesContainer}>
        {messages && messages.map(msg => <ChatMessage key={msg.id} message={msg}/>)}
      </View>
    </View>
    
  )
}

const stylesChatBox = StyleSheet.create({
  chatRoomContainer: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center'
  },
  messagesContainer:{
    flex: 1,
    backgroundColor: ' #85929e '
  }
});
