import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState } from 'react';
import { StyleSheet, Text, View } from 'react-native';

import { createAppContainer } from 'react-navigation'
import { createStackNavigator } from 'react-navigation-stack'
import ChatBox from './screens/ChatBox';
import LoginScreen from './screens/LoginScreen';

import AuthContext from './contexts/AuthContext'

import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth';
import { useAuthState } from 'react-firebase-hooks/auth'
import NavBar from './components/NavBar';

var auth;


function initialiseFirebase(){
  firebase.initializeApp({
    apiKey: "AIzaSyAwoq0tivHGlinmnkqf3QCEQWCMuvccKCg",
    authDomain: "reactnativechatapp-d07dc.firebaseapp.com",
    projectId: "reactnativechatapp-d07dc",
    storageBucket: "reactnativechatapp-d07dc.appspot.com",
    messagingSenderId: "833518837423",
    appId: "1:833518837423:web:1d1e6790c3c0ebd416be5a",
    measurementId: "G-5WJ3ZD1GP6"
  })
  auth = firebase.auth();
}



const MainNavigator = createStackNavigator(
  {
    Chat: ChatBox
  },
  {
    headerMode: 'none'
  }
)
const AppNavigator = createAppContainer(MainNavigator)

export default async function App() {
  const firestore = firebase.firestore()
  const [user] =await useAuthState(auth);
  const value = { auth, firestore }
  return (
    <View style={styles.container}>
      <AuthContext.Provider value={value}>
        <NavBar user={user} />
        {user ? <ChatBox /> : <LoginScreen auth={auth} />}
      </AuthContext.Provider>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'center',
    backgroundColor: '#125495'
  },
});
