import React, { useContext, useEffect, useRef, useState } from 'react';
import { StyleSheet, Text, View, Image, Dimensions, ScrollView } from 'react-native';
import AuthContext from '../contexts/AuthContext';
import ChatMessage from '../components/ChatMessage';
import SendMessageForm from '../components/SendMessageForm';
import stylesChatBox from '../styles/chatBoxStyle'
import 'firebase/firestore';

import { useCollectionData } from 'react-firebase-hooks/firestore';

const io = require('socket.io-client')
const socketEndpoint = 'http://192.168.10.118:4167'

export default function ChatBox() {
  //firebase user auth
  const {auth, firestore} = useContext(AuthContext);
  const {displayName, photoURL} = auth.currentUser

  //firestore setup
  const messageRef = firestore.collection('messages');
  const query = messageRef.orderBy('createdAt', 'desc').limit(25);
  const [messagesFromFS] = useCollectionData(query, {idField: 'id'})
  var messages = messagesFromFS.reverse()

  //socket.io states
  const [connected, setConnected] = useState('false');
  const [pingData, setPingData] = useState('');



  useEffect(() => {

    var socket = io(socketEndpoint);
    socket.on('connectClient', () => {
      setConnected('true');
    })
    socket.on('ping', (data) => {
      setPingData(data.date);
    })

  }, [])

  //sign out firebase user
  const SignOut = () => {
    return auth.currentUser && (
      <button onClick={() => auth.signOut()}>Sign Out</button>
    )
  }

  const ShowMessages = () => {
    return messages && messages.map(msg => <ChatMessage key={msg.id} message={msg}/>)
  
  }
  var scrollViewRef = useRef(null);
  
  return (
    <View style={stylesChatBox.chatRoomContainer}>
      
      <Text>Chat Screen</Text>
      <Text>{displayName}</Text>
      <Image style={{width: 100, height: 100 }} source={{uri: photoURL}}/>
      {SignOut()}

      <ScrollView contentContainerStyle={stylesChatBox.messagesContainer} ref={scrollViewRef}
             onChange={() => scrollViewRef.scrollToEnd({animated: true})}>
        {ShowMessages()}
      </ScrollView>

      <SendMessageForm messageRef={messageRef} firestore={firestore}/>

    </View>
    
  )
}
