import React, { useContext } from 'react'
import { View, StyleSheet, Text, Dimensions, Image, TouchableOpacity } from 'react-native'
import AuthContext from '../contexts/AuthContext';

export default function NavBar(props) {
    const userInfo = () => {
        if (props.user != null) {
            const { auth } = useContext(AuthContext);
            const { displayName, photoURL } = auth.currentUser

            return (
                <View styles={styles.UserInfoContainer}>
                    <Text style={styles.UserNameTitle}>{displayName}</Text>
                    <Image style={styles.UserProfilePic} source={{ uri: photoURL }}></Image>
                </View>
            )
        }
    }
    const SignOut = () => {
        if (props.user != null) {
            const { auth } = useContext(AuthContext);
            return auth.currentUser && (
                <TouchableOpacity styleonPress={() => auth.signOut()}>
                    Sign Out
                </TouchableOpacity>
            )
        }
    }
    return (
        <View style={styles.NavContainer}>
            <Text style={styles.LogoText}>Chatter</Text>
            {userInfo()}
        </View>
    )
}
const styles = StyleSheet.create({
    NavContainer: {
        flex: 1,
        flexDirection: 'row',
        backgroundColor: '#2DA8D8FF',
        justifyContent: 'center',
        alignItems: 'center',
        maxHeight: 0.1 * Dimensions.get('window').height
    },
    LogoText: {
        fontSize: 40,
        color: '#fff',
        marginLeft: '1em',
        
    },
    UserInfoContainer: {
        minWidth: 'auto',
        marginLeft: 'auto',

    },
    UserNameTitle: {
        fontSize: 20,
        color: '#fff',
        marginLeft: 'auto',
    },
    UserProfilePic: {
        width: '50px',
        height: '50px',
        borderRadius: 25
    }

})
