import React, { useState, useContext } from 'react'
import { StyleSheet, Text, View, TextInput, TouchableOpacity, Image, useWindowDimensions} from 'react-native'
import firebase from 'firebase'
import 'firebase/auth'

export default function LoginScreen(props) {

    const signInWithGoogle = () =>{
        const provider = new firebase.auth.GoogleAuthProvider();
        props.auth.signInWithPopup(provider);
    }
    const {width, height} = useWindowDimensions()
    const styles = StyleSheet.create({
        LoginContainer :{
            flex : 1,
            flexDirection: 'column',
            backgroundColor : '#2A2B2DFF',
            alignItems : 'center',
            justifyContent : 'flex-start',
            minHeight: 0.93*height,
            maxHeight: 0.93*height
            
        },
        LoginTitle: {
            fontSize: 40, 
            color: '#fff',
            marginTop: '3em'
        },  
        loginButton: {
            backgroundColor: '#D9514EFF',
            borderRadius: 25,
            marginTop: 'auto',
            marginBottom: '5em'
            
            
        },
        LoginText: {
            padding: 10,
            color: '#fff',
            fontSize: 30
    
        }
    
    })

    return (
        <View style={styles.LoginContainer}>
            <Text style={styles.LoginTitle}>Login</Text>
            <TouchableOpacity onPress={signInWithGoogle} style={styles.loginButton}>
                <Text style={styles.LoginText}>Sign in with Google</Text>
            </TouchableOpacity>
        </View>
    )
}
