import { StyleSheet, Dimensions } from 'react-native';

const stylesChatBox = StyleSheet.create({
    chatRoomContainer: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        maxHeight: Dimensions.get('window').height,

    },
    messagesContainer: {
        flex: 1,
        backgroundColor: '#85929e',
        alignItems: 'left',
        justifyContent: 'left',
        paddingLeft: 10,
        paddingRight: 10,
        height: 0.5 * Dimensions.get('window').height,
        width: 0.8 * Dimensions.get('window').width,


    },
    SentMessage: {
        fontSize: 30,
        backgroundColor: '#f39c12',
        color: '#fff',
        alignSelf: 'flex-end',
        marginRight: 10
    },
    ReceivedMessage: {
        flex: 1,
        flexDirection: 'row',
        fontSize: 30,
        backgroundColor: '#3498db',
        color: '#fff',
        marginLeft: 10,
        maxWidth: 500,
        borderRadius: 25,
        padding: 1
    }
});
export default stylesChatBox;
