import React, { useContext, useState } from 'react'
import { View, StyleSheet, Text, Dimensions, Image, TouchableOpacity } from 'react-native'
import AuthContext from '../contexts/AuthContext';

export default function NavBar(props) {

    const [dropdownVisible, setDropdownVisible] = useState(false)
    const userInfo = () => {
        if (props.user != null) {
            const { auth } = useContext(AuthContext);
            const { displayName, photoURL } = auth.currentUser

            return (
                <View style={styles.UserInfoContainer}>
                    <TouchableOpacity onPress={() => setDropdownVisible(true)}>
                        <Image style={styles.UserProfilePic} source={{ uri: photoURL }}></Image>
                    </TouchableOpacity>
                    <Text style={styles.UserNameTitle}>{displayName}</Text>
                </View>
            )
        }
    }

    const testClick = (bool) =>{

    }
    const SignOut = () => {
        if (props.user != null) {
            const { auth } = useContext(AuthContext);
            return auth.currentUser && (
                <View style={styles.AccountDropdownView}>
                    <TouchableOpacity onPress={() => auth.signOut()}>
                        Sign Out
                    </TouchableOpacity>
                </View>
            )
        }
    }
    return (
        <View style={styles.NavContainer}>
            <Text style={styles.LogoText}>Chatter</Text>
            {userInfo()}
            {dropdownVisible ? null : () => SignOut()}
        </View>
    )
}
const styles = StyleSheet.create({
    NavContainer: {
        flex: 1,
        flexDirection: 'row',
        backgroundColor: '#2DA8D8FF',
        justifyContent: 'flex-start',
        alignItems: 'center',
        maxHeight: 0.07 * Dimensions.get('window').height,
        maxWidth: Dimensions.get('window').width
    },
    LogoText: {
        fontSize: 40,
        color: '#fff',
        marginLeft: '0.5em'
        
    },
    UserInfoContainer: {
        flex: 1,
        flexDirection: 'row-reverse',
        justifyContent: 'flex-start',
        alignItems: 'center',
        marginLeft: 'auto',
        marginRight: '1em'


    },
    UserNameTitle: {
        paddingRight: '1em',
        fontSize: 20,
        color: '#fff',

    },
    UserProfilePic: {
        width: '50px',
        height: '50px',
        borderRadius: 25
    }

})
