import React, { useContext, useEffect, useRef, useState } from 'react';
import { StyleSheet, Text, View, Image, Dimensions, ScrollView, useWindowDimensions } from 'react-native';
import AuthContext from '../contexts/AuthContext';
import ChatMessage from '../components/ChatMessage';
import SendMessageForm from '../components/SendMessageForm';
//import stylesChatBox from '../styles/chatBoxStyle'
import 'firebase/firestore';

import { useCollectionData } from 'react-firebase-hooks/firestore';

const io = require('socket.io-client')
const socketEndpoint = 'http://192.168.10.118:4167'


export default function ChatBox() {
  const {width, height} = useWindowDimensions()
  const [loading, setLoading] = useState(true);
  //firebase user auth
  const { auth, firestore } = useContext(AuthContext);
  //firestore setup
  const messageRef = firestore.collection('messages');
  const query = messageRef.orderBy('createdAt', 'desc').limit(25);
  const [messages] = useCollectionData(query, { idField: 'id' })
  console.log('hello there')
  //socket.io states
  const [connected, setConnected] = useState('false');
  const [pingData, setPingData] = useState('');

  useEffect(() => {
    setLoading(false);
    if(loading ==false){
      scrollViewRef.current.scrollToEnd({ animated: false })
    }

    var socket = io(socketEndpoint);
  }, [])


  const ShowMessages = () => {
    return messages && messages.slice(0).reverse().map(msg => <ChatMessage key={msg.id} message={msg} />)

  }
  const scrollViewRef = useRef();

  const stylesChatBox = StyleSheet.create({
    chatRoomContainer: {
        flex: 1,
        backgroundColor: '#2A2B2DFF',
        alignItems: 'center',
        justifyContent: 'center',
    },
    messagesContainer: {
        flex: 1,
        flexDirection: 'column',
        flexGrow: 1,
        justifyContent: 'flex-start',
        maxWidth: width,
        maxHeight: 0.86*height

    },
    SentMessageContainer: {
        flex: 1,
        flexDirection: 'row-reverse',
        justifyContent: 'flex-start',
        alignItems: 'center',
        marginTop: '1em',
        marginBottom: '1em',
        marginLeft: '10em',
        marginRight: '1em',
        minHeight: 'auto',
    },
    SentMessageText:{
        fontSize: 15,
        backgroundColor: '#2DA8D8FF',
        color: 'black',
        marginRight: 10,
        borderRadius: 25,
        padding: 8,
        alignSelf: 'center',
        maxWidth: '100%'
    },
    ReceivedMessageContainer: {
        flex: 1,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'flex-start',
        marginTop: '1em',
        marginBottom: '1em',
        marginLeft: '1em',
        marginRight: '10em',
        flexGrow: 1,
        minHeight: 'auto'
 
    },
    ReceivedMessageText: {
        fontSize: 15,
        backgroundColor: '#D9514EFF',
        color: '#fff',
        marginLeft: 10,
        borderRadius: 25,
        padding: 8,
        alignSelf: 'center',
        maxWidth: '100%'
    },
    profileImage :{
        width: 50,
        height: 50,
        borderRadius: 25,
        alignSelf: 'center'
    }
});

  return (
    <View style={{minHeight: 0.93*height}}>
      { loading ? <Text>Loading...</Text> :
        <View style={stylesChatBox.chatRoomContainer}>

          <ScrollView
            contentContainerStyle={stylesChatBox.messagesContainer}
            ref={scrollViewRef}>

            {ShowMessages()}
          </ScrollView>
        </View>
      }
      <SendMessageForm messageRef={messageRef} firestore={firestore} />
    </View>


  )
}
