import React, { useEffect, useState } from 'react'
import { StyleSheet, Text, View } from 'react-native';

const io = require('socket.io-client')
const socketEndpoint = 'http://192.168.10.118:4167'


export default function ChatBox(props) {
      
  const [connected, setConnected] = useState('false')
  const [pingData, setPingData] = useState('')
  useEffect(() => {
    var socket = io(socketEndpoint)
    socket.on('connectClient', () => {
        setConnected('true')
      })
    socket.on('ping', (data) =>{
        setPingData(data.date)
      })
  }, [])

    return(
        <View style={stylesChatBox.chatContainer}>
            <Text>Chat Screen</Text>
        </View>
    )
}

const stylesChatBox = StyleSheet.create({
    chatContainer: {
      flex: 1,
      backgroundColor: '#402010',
      alignItems: 'center',
      justifyContent: 'center',
      color: '#fff'
    }
});
