import React, { useContext, useState } from 'react'
import { View, StyleSheet, Text, Dimensions, Image, TouchableOpacity, useWindowDimensions} from 'react-native'
import AuthContext from '../contexts/AuthContext';
import SideBarMenu from './SideBarMenu';

export default function NavBar(props) {
    const {width, height} = useWindowDimensions()
    const [dropdownVisible, setDropdownVisible] = useState(false)
    const [showMenu, setShowMenu] = useState(false)
    const userInfo = () => {
        if (props.user != null) {
            const { auth } = useContext(AuthContext);
            const { displayName, photoURL } = auth.currentUser

            return (
                <View style={styles.UserInfoContainer}>
                    <TouchableOpacity onPress={() => setDropdownVisible(!dropdownVisible)}>
                        <Image style={styles.UserProfilePic} source={{ uri: photoURL }}></Image>
                    </TouchableOpacity>
                    <Text style={styles.UserNameTitle}>{displayName}</Text>
                </View>
            )
        }
    }


    const SignOut = () => {
        if (props.user != null) {
            const { auth } = useContext(AuthContext);
            return auth.currentUser && (
                <View style={styles.AccountDropdownView}>
                    <TouchableOpacity style={styles.SignOutButton} onPress={() => auth.signOut()}>
                        Sign Out
                    </TouchableOpacity>
                </View>
            )
        }
    }

    const styles = StyleSheet.create({
        NavContainer: {
            position: 'relative',
            top: 0,
            flex: 1,
            flexDirection: 'row',
            backgroundColor: '#2DA8D8FF',
            justifyContent: 'flex-start',
            alignItems: 'center',
            minWidth: width,
            minHeight: 0.07*height,
            maxHeight: 0.07*height
        },
        LogoText: {
            fontSize: '2em',
            color: '#fff',
            marginLeft: '0.5em'
    
        },
        UserInfoContainer: {
            flex: 1,
            flexDirection: 'row-reverse',
            justifyContent: 'flex-start',
            alignItems: 'center',
            marginLeft: 'auto',
            marginRight: '1em'
    
    
        },
        UserNameTitle: {
            paddingRight: '1em',
            fontSize: '1em',
            color: '#fff',
    
        },
        UserProfilePic: {
            width: '50px',
            height: '50px',
            borderRadius: 25
        },
        AccountDropdownView: {
            marginRight: '1em',
            flex: 1,
            flexDirection: 'column',
            alignItems: 'center',
            backgroundColor: '#fff',
            maxWidth: '5em',
            borderRadius: 10
    
    
        },
        SignOutButton: {
            fontSize: '0.9em',
            padding: 10
        },
        OpenMenuButton: {
            flex:1,
            justifyContent: 'center',
            alignItems: 'center',
            fontSize: 30,
            marginRight: 'auto',
            marginLeft: '0.5em',
            backgroundColor: '#2DA8D8FF',
            width: 40,
            maxWidth: 40,
            height: 40,
            borderRadius: 25
        },
        OpenMenuText: {
            fontSize: 20,
            color: '#fff',
            padding: 5
        }
    })

    return (
        <View style={styles.NavContainer}>
            <TouchableOpacity onPress={SideBarMenu(true)} style={styles.OpenMenuButton}> 
                <Text style={styles.OpenMenuText}>☰</Text>
            </TouchableOpacity>
            {showMenu ? <SideBarMenu/>: null}
            <Text style={styles.LogoText}>Chatter</Text>
            {userInfo()}
            {dropdownVisible ? SignOut() : null}
        </View>
    )
}
