import React, {useContext} from 'react'
import {View, StyleSheet, Text, Dimensions, Image} from 'react-native'
import AuthContext from '../contexts/AuthContext';

export default function NavBar(props){
    const userInfo = () =>{
        if(props.user != null){
            const {auth} = useContext(AuthContext);
            const {displayName, photoURL} = auth.currentUser

            return(
                <View styles={styles.UserInfoContainer}>
                    <Text styles={styles.UserNameTitle}>{displayName}</Text>
                    <Image style={styles.UserProfilePic} source={{uri: photoURL}}></Image>
                </View>
            )
        }
    }
    return(
        <View style={styles.NavContainer}>
            <Text style={styles.LogoText}>Chatter</Text>
            {userInfo()}
        </View>
    )
}
const styles = StyleSheet.create({
    NavContainer : {
        flex: 1,
        flexDirection: 'row',
        backgroundColor: '#2DA8D8FF',
        justifyContent: 'flex-start',
        alignItems: 'center',
        maxHeight:  0.07*Dimensions.get('window').height
    },
    LogoText : {
        fontSize: 40,
        color: '#fff',
        marginLeft: '1em'
    },
    UserNameTitle : {
        fontSize: 40,
        color: '#fff',
        marginLeft: '1em'
    }

})
