import React, { useRef } from 'react'
import { StyleSheet, Text, TouchableOpacity, View, useWindowDimensions, Animated, } from 'react-native'

export default function SideBarMenu(props) {

    const { width, height } = useWindowDimensions();
    var widthModifier = 0.6;
    if (width < 1280) {
        widthModifier = 0.8;
    }
    else {
        widthModifier = 0.3;
    }

    var slideAnim = useRef(new Animated.Value(-widthModifier * width * 2)).current;

    const slideIn = () => {
        Animated.timing(slideAnim, {
            toValue: 0,
            duration: 500
        }).start();
    };
    const slideOut = () => {
        Animated.timing(slideAnim, {
            toValue: -widthModifier* 1.1 * width,
            duration: 500
        }).start();
    };

    if(props.showMenu == true){
        slideIn()
    }
    const slideOutView = () =>{
        props.setShowMenu(false)
        slideOut()
    }

    const styles = StyleSheet.create({
        SideBarContainer: {
            flex: 1,
            flexDirection: 'column',
            justifyContent: 'flex-start',
            alignItems: 'center',
            position: 'absolute',
            top: 0,
            backgroundColor: '#566573',
            minWidth: widthModifier * width,
            maxWidth: widthModifier * width,
            minHeight: height,
            maxHeight: height,
            zIndex: 20,
            borderTopRightRadius: 20,
            borderBottomRightRadius: 20,
            shadowColor: '#000',
            shadowOffset: { width: 3, height: 0 },
            shadowOpacity: 0.7,
            shadowRadius: 50,
            elevation: 5
        },
        SiderBarHeader: {
            flex: 1,
            flexDirection: 'row',
            justifyContent: 'center',
            alignItems: 'center',
            minHeight: 0.1 * height,
            maxHeight: 0.1 * height,
            width: widthModifier * width
        },
        MenuTitleText: {
            fontSize: 25,
            color: '#fff',
            marginRight: 'auto',


        },
        MenuItemsContainer: {
            flex: 1,
            flexDirection: 'column',
            justifyContent: 'flex-start',
            alignItems: 'center',
            minHeight: widthModifier * height,
            maxHeight: widthModifier * height,
            minWidth: 0.6 * width,
            maxWidth: 0.6 * width,

        },
        MenuItem: {
            flex: 1,
            flexDirection: 'column',
            justifyContent: 'center',
            alignItems: 'center',
            backgroundColor: '#2DA8D8FF',
            minWidth: (widthModifier - 0.01) * width,
            maxWidth: (widthModifier - 0.01) * width,
            minHeight: 0.05 * height,
            maxHeight: 0.05 * height,
            marginBottom: 20,
            borderRadius: 25,

        },
        MenuItemText: {
            fontSize: 20,
            color: '#fff'
        },
        CloseMenuButton: {
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center',
            fontSize: 30,
            marginRight: 'auto',
            marginLeft: '0.5dp',
            backgroundColor: '#2DA8D8FF',
            width: 40,
            maxWidth: 40,
            height: 40,
            borderRadius: 25
        },
        CloseMenuText: {
            fontSize: 20,
            color: '#fff',
            padding: 5
        }

    });

    return (
        <Animated.View style={[styles.SideBarContainer, { left: slideAnim }]}>
            <View style={styles.SiderBarHeader}>
                <TouchableOpacity onPress={() =>slideOutView()} style={styles.CloseMenuButton}>
                    <Text style={styles.CloseMenuText}>X</Text>
                </TouchableOpacity>
                <Text style={styles.MenuTitleText}>Menu</Text>
            </View>
            <View style={styles.MenuItemsContainer}>
                <TouchableOpacity style={styles.MenuItem}>
                    <Text style={styles.MenuItemText}>Join Room</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.MenuItem}>
                    <Text style={styles.MenuItemText}>Create Room</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.MenuItem}>
                    <Text style={styles.MenuItemText}>Dummy Option</Text>
                </TouchableOpacity>
            </View>
        </Animated.View>
    )
}
