import { StyleSheet, Dimensions } from 'react-native';

const stylesChatBox = StyleSheet.create({
    chatRoomContainer: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
        maxHeight: Dimensions.get('window').height,

    },
    messagesContainer: {
        flex: 1,
        flexDirection: 'column',
        flexShrink: 0,
        justifyContent: 'flex-start',
        backgroundColor: '#85929e',
        height: 0.7*Dimensions.get('window').height,
        width:0.6*Dimensions.get('window').width,


    },
    SentMessageContainer: {
        flex: 1,
        flexDirection: 'row-reverse',
        justifyContent: 'flex-start',
        alignItems: 'center',
        marginTop: 10,
        height: '100%'
    },
    SentMessageText:{
        fontSize: 20,
        backgroundColor: '#f39c12',
        color: 'black',
        marginRight: 10,
        maxWidth: 500,
        borderRadius: 25,
        padding: 5,
        alignSelf: 'center'
    },
    ReceivedMessageContainer: {
        flex: 1,
        flexDirection: 'row',
        alignItems: 'flex-start',
        justifyContent: 'center',
        top: 0,
        marginTop: 50
 
    },
    ReceivedMessageText: {
        fontSize: 20,
        backgroundColor: '#3498db',
        color: '#fff',
        marginLeft: 10,
        maxWidth: 500,
        borderRadius: 25,
        padding: 5
    },
    profileImage :{
        width: 50,
        height: 50,
        borderRadius: 25,
        alignSelf: 'center'
    }
});
export default stylesChatBox;
