import React, { useContext, useEffect, useRef, useState } from 'react';
import { StyleSheet, Text, View, Image, Dimensions, ScrollView, useWindowDimensions,PixelRatio } from 'react-native';
import AuthContext from '../contexts/AuthContext';
import ChatMessage from '../components/ChatMessage';
import SendMessageForm from '../components/SendMessageForm';
//import stylesChatBox from '../styles/chatBoxStyle'
import 'firebase/firestore';

import { useCollectionData } from 'react-firebase-hooks/firestore';

const io = require('socket.io-client')
const socketEndpoint = 'http://192.168.10.118:4167'


export default function ChatBox(props) {
  const {width, height} = useWindowDimensions()
  const fontRatio = PixelRatio.getFontScale()
  const [loaded, setLoaded] = useState(false)

  //firebase user auth
  const { auth, firestore } = useContext(AuthContext);
  //firestore setup


  //socket.io states
  const [connected, setConnected] = useState('false');
  const [pingData, setPingData] = useState('');
  
  const messageRef = firestore.collection('messages' + props.roomCode);
  const query = messageRef.orderBy('createdAt', 'desc').limit(25);
  var [messages] = [messages] = useCollectionData(query, { idField: 'id' })


  const scrollViewRef = useRef();

  useEffect(() => {
    scrollViewRef.current.scrollToEnd({ animated: false })
    //var socket = io(socketEndpoint);
  },[])


  const ShowMessages = () => {
      return messages && messages.slice(0).reverse().map(msg => <ChatMessage key={msg.id} message={msg} />)
  }


  const stylesChatBox = StyleSheet.create({
    chatRoomContainer: {
        flex: 1,
        backgroundColor: '#2A2B2DFF',
        alignItems: 'center',
        justifyContent: 'center',
    },
    messagesContainer: {
        flex: 1,
        flexDirection: 'column',
        flexGrow: 0,
        justifyContent: 'flex-start',
        minWidth: width,
        maxWidth: width,
        maxHeight: 0.86*height,
        minHeight: 0.86*height,
        padding: 10 * fontRatio

    },
    SentMessageContainer: {
        flex: 1,
        flexDirection: 'row-reverse',
        justifyContent: 'flex-start',
        flexShrink: 1,
        alignItems: 'center',
        marginTop: 20 * fontRatio,
        marginBottom: 10* fontRatio,
        marginLeft: 50* fontRatio,
        marginRight: 10* fontRatio,

    },
    SentMessageText:{
        fontSize: 15,
        backgroundColor: '#2DA8D8FF',
        color: 'black',
        marginRight: 10* fontRatio,
        borderRadius: 0,
        padding: 8* fontRatio,
        maxWidth: 0.1* width
    },
    ReceivedMessageContainer: {
        flex: 1,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'flex-start',
        marginTop: 10* fontRatio,
        marginBottom: 1* fontRatio,
        marginLeft: 1* fontRatio,
        marginRight: 10* fontRatio,
        minHeight: 'auto'
 
    },
    ReceivedMessageText: {
        fontSize: 15,
        backgroundColor: '#D9514EFF',
        color: '#fff',
        marginLeft: 10* fontRatio,
        borderRadius: 25,
        padding: 8* fontRatio,
        alignSelf: 'center',
        maxWidth: 100
    },
    profileImage :{
        width: 50* fontRatio,
        height: 50* fontRatio,
        borderRadius: 25,
        alignSelf: 'center'
    }
});

  return (
    <View style={{minHeight: 0.93*height, maxHeight: 0.93*height,
      top:0.07 * height, left: 0, position:'absolute',}}>
        <View style={stylesChatBox.chatRoomContainer}>
          <ScrollView
            contentContainerStyle={stylesChatBox.messagesContainer}
            ref={scrollViewRef}>

            {ShowMessages()}
          </ScrollView>
        </View>
      <SendMessageForm messageRef={messageRef} firestore={firestore} />
    </View>


  )
}
