import React, { useEffect, useState } from 'react';
import { StyleSheet, Text, View, useWindowDimensions, Button, Platform } from 'react-native';

import { createAppContainer } from 'react-navigation'
import { createStackNavigator } from 'react-navigation-stack'
import ChatBox from './screens/ChatBox';
import LoginScreen from './screens/LoginScreen';

import AuthContext from './contexts/AuthContext'


import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth';

import * as FirebaseCore from 'expo-firebase-core';

import { useAuthState } from 'react-firebase-hooks/auth'
import NavBar from './components/NavBar';
import * as GoogleSignIn from 'expo-google-sign-in';

if (!firebase.apps.length) {
  firebase.initializeApp(FirebaseCore.DEFAULT_APP_OPTIONS);
}else {
  firebase.app(); // if already initialized, use that one
}
const auth = firebase.auth();
const firestore = firebase.firestore()

const MainNavigator = createStackNavigator(
  {
    Chat: ChatBox
  },
  {
    headerMode: 'none'
  }
)
const AppNavigator = createAppContainer(MainNavigator)


export default function App() {

  const { width, height } = useWindowDimensions()
  const [userMobile, setUserMobile] = useState(null);
  const [user, loading] = useAuthState(auth);
  const value = { auth, firestore }

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      flexDirection: 'column',
      flexGrow: 0,
      justifyContent: 'center',
      backgroundColor: '#125495',
      maxWidth: width,
      maxHeight: height,

    },
  });

  //////////////google sign in stuff

  useEffect(() => {
    if(Platform.OS == 'android' || Platform.OS== 'ios'){
      initAsync()
    }
  })

  const initAsync = async () => {
    await GoogleSignIn.initAsync();
    _syncUserWithStateAsync();
  };

  const _syncUserWithStateAsync = async () => {
    const userMobileNew = await GoogleSignIn.signInSilentlyAsync();
    setUserMobile(userMobileNew)
  };


  const signOutAsync = async () => {
    await GoogleSignIn.signOutAsync();
    setUserMobile(null)
  };
  const signInAsync = async () => {
    try {
      await GoogleSignIn.askForPlayServicesAsync();
      const { type, user } = await GoogleSignIn.signInAsync();
      if (type === 'success') {
        _syncUserWithStateAsync();
        console.log('signed in')
      }
    } catch ({ message }) {
      alert('login: Error:' + message);
    }
  };


  return (
    <View style={styles.container}>
        <AuthContext.Provider value={value}>
          <NavBar user={user} />
          {user ? <ChatBox roomCode={'$ab348skd3'} /> : <LoginScreen auth={auth} googleLogin={signInAsync} />}
        </AuthContext.Provider>
    </View>
  )
}
